<h3>Migrating your site to Pixely couldn’t be easier</h3>
<div class="field">
    <div class="control has-tooltip">
        <label for="EMAIL">Your email</label>
         <div class="is-primary tooltip is-tooltip-bottom is-tooltip-multiline pxl-tip" data-tooltip="This is your Pixely's registered email address.">
            <div class="icon-help_outline"></div>
        </div>
        <input v-model="email" data-vv-validate-on="change" v-validate="{ required: true, email }" name="EMAIL" type="email" :class="{'input': true, 'is-danger': errors.has('EMAIL') }" class="input is-medium email required" placeholder="Enter your email address">
        <i v-show="errors.has('EMAIL')" class="fa fa-warning"></i>
        <span v-show="errors.has('EMAIL')" class="help is-danger">{{ errors.first('EMAIL') }}</span>
    </div>
</div>

<div>
  <div class="control has-tooltip">
      <label for="CURRENTURL">Current site URL</label>
      <div class="is-primary tooltip is-tooltip-bottom is-tooltip-multiline pxl-tip" data-tooltip="Please enter your current website domain name.">
          <div class="icon-help_outline"></div>
      </div>
      <input id="mce-CURRENTURL" name="CURRENTURL" v-model="CURRENTURL" data-vv-validate-on="change" v-validate="{ required: true, url:true}" :class="{'input': true, 'is-danger': errors.has('CURRENTURL') }" class="input is-medium" type="url" placeholder="eg. pixely.com">
      <i v-show="errors.has('CURRENTURL')" class="fa fa-warning"></i>
      <span v-show="errors.has('CURRENTURL')" class="help is-danger">{{ errors.first('CURRENTURL') }}</span>
  </div>
</div>

<div class="field">
    <div class="control input-domain-wrapper">
        <label for="input-domain">Do you have a site on Pixely you want us to overwrite?</label>
        <div class="control migration">
            <div class="dk">
                <input class="mig-no" type="radio" id="no" name="radio-group" v-on:change="isOpen = false" checked>
                <label for="no">No</label>
            </div>
            <div class="dk">
                <input class="mig-yes" type="radio" id="yes" name="radio-group" v-on:change="isOpen = true">
                <label for="yes">Yes</label>
            </div>
        </div>
    </div>
</div>

<input id="OVERWRITE" name="OVERWRITE" type="hidden" value="No" v-if="!isOpen">
<input id="OVERWRITE" name="OVERWRITE" type="hidden" value="Yes" v-if="isOpen">

<div class="field">
    <div class="control has-tooltip">
        <label for="INPUTSITEN" v-if="!isOpen">Site name</label>
        <label for="INPUTSITEN" v-if="isOpen">Which site?</label>
        <div class="is-primary tooltip is-tooltip-bottom is-tooltip-multiline pxl-tip" data-tooltip="This is the name for your new site. You can change this later but it will likely just be your name or the name of your company, group or organisation.">
            <div class="icon-help_outline"></div>
        </div>
        <input name="INPUTSITEN" v-model="inputSiteName" data-vv-validate-on="change" v-validate="{ required: true, min:2, regex:/^[A-Za-z0-9- ]+$/}" :class="{'input': true, 'is-danger': errors.has('INPUTSITEN') }" class="input is-medium" type="text" placeholder="Enter your site's name" id="mce-INPUTSITEN">
        <i v-show="errors.has('INPUTSITEN')" class="fa fa-warning"></i>
        <span v-show="errors.has('INPUTSITEN')" class="help is-danger">{{ errors.first('INPUTSITEN') }}</span>
    </div>
</div>

<p class="footnote">Our migration includes the database, media files, plugins and themes.</p>

<div>
<hr>
<h3 class="wp-new-user">Your existing Wordpress credentials</h3>
<div class="columns wp-new-user-details">
  <div class="column">
    <div class="field">
      <div class="control">
          <label for="WPADMIN">WordPress admin username</label>
          <input name="WPADMIN" id="mce-WPADMIN" v-model="WPADMIN" data-vv-validate-on="change" v-validate="{required: true, min:1, alpha_num:true}" :class="{'input': true, 'is-danger': errors.has('WPADMIN') }" class="input is-medium username" type="text" placeholder="Choose a username" autocapitalize="off">
          <i v-show="errors.has('WPADMIN')" class="fa fa-warning"></i>
          <span v-show="errors.has('WPADMIN')" class="help is-danger">{{ errors.first('WPADMIN') }}</span>
      </div>
  </div>
</div>
<div class="column">
  <div class="field userpass">
      <div class="control">
          <label for="WPPASSWORD">WordPress admin password</label>
          <div class="showhidepassword">
              <div class="icon-visibility" id="svg-show"></div>
              <div class="icon-visibility_off" id="svg-hide"></div>
          </div>
          <input name="WPPASSWORD" id="mce-WPPASSWORD" v-model="WPPASSWORD" data-vv-validate-on="change" v-validate=" { required: true, min:1, regex: /^[^_\s]*$/}" data-vv-as="Password" :class="{'input': true, 'is-danger': errors.has('WPPASSWORD') }" class="input password is-medium" type="password" placeholder="Choose a password">
          <i v-show="errors.has('WPPASSWORD')" class="fa fa-warning"></i>
          <span v-show="errors.has('WPPASSWORD')" class="help is-danger">{{ errors.first('WPPASSWORD') }}</span>
      </div>
    </div>
